import * as React from 'react';
import WelcomeContent from './WelcomeContent';
import AuthContent from './AuthContent';
import LoginForm from './LoginForm';
import Buttons from './Buttons';
import { request, setAuthHeader } from '../axios_helper';

export default class AppContent extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      componentToShow: "welcome"
    };
  };

  login = () => {
    this.setState({ componentToShow: "login" });
    console.log("tela de login");
  }

  logout = () => {
    this.setState({ componentToShow: "welcome" });
  }
  

  onLogin = async (e, login, password) => {
   
    e.preventDefault();
  
  
    if (!login) {
      this.setState({componentToShow: "loginNull"})

      return;
    }
  
    try {
      const response = await request("POST", "/auth/login", { login: login, password: password }, { cache: 'no-store' });
  
      console.log("Resposta do servidor:", response); // Adicione esta linha para verificar a resposta do servidor

  
    } catch (err) {
      // Trate os erros aqu
      console.log("Erro ao fazer login:", err.message);
      this.setState({ componentToShow: "welcome" });
    }
  };
  
  

  onRegister = (event, login, password, role) => {
    event.preventDefault();
    request(
        "POST",
        "/auth/register",
        {
            login: login,
            password: password,
            role: role
        }).then(
        (response) => {
            setAuthHeader(response.data.token);
            this.setState({componentToShow: "messages"});
        }).catch(
        (error) => {
            setAuthHeader(null);
            this.setState({componentToShow: "welcome"})
        }
    );
};


  render() {
    return (
      <div>
        <Buttons login={this.login} logout={this.logout}/>
        {this.state.componentToShow === "welcome"  && <WelcomeContent />}
        {this.state.componentToShow === "loginNull"  && <WelcomeContent />}
        {this.state.componentToShow === "messages"  && <AuthContent />}
        {this.state.componentToShow === "login"  && <LoginForm onLogin={this.onLogin} onRegister={this.onRegister} />}
      </div>
    );

  };

}
