import * as React from 'react';
import WelcomeContent from './WelcomeContent';
import AuthContent from './AuthContent';
import LoginForm from './LoginForm';
import Buttons from './Buttons';
import { request, setAuthHeader } from '../axios_helper';
import LoginNull from './LoginNull';

export default class AppContent extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      componentToShow: "welcome",
      isLoginNullVisible: false, // Estado para controlar a visibilidade do pop-up de aviso
    };
  };

  login = () => {
    this.setState({ componentToShow: "login" });
    console.log("tela de login");
  }

  logout = () => {
    this.setState({ componentToShow: "welcome" });
  }
  
  // Função para exibir o pop-up de aviso quando o campo de login estiver vazio
  showLoginNullPopup = () => {
    this.setState({ isLoginNullVisible: true });
  };

  // Função para fechar o pop-up de aviso
  closeLoginNullPopup = () => {
    this.setState({ isLoginNullVisible: false });
  };

  onLogin = async (e, login, password) => {
    e.preventDefault();
  
    if (!login || !password) {
      this.showLoginNullPopup();
      return;
    }
  
    try {
      const response = await request("POST", "/auth/login", { login: login, password: password }, { cache: 'no-store' });
      console.log("Resposta do servidor:", response);

      if (response.token) {
        // Autenticação bem-sucedida
        setAuthHeader(response.token);
        this.setState({ componentToShow: "messages" });
        console.log("Autenticado com sucesso");
      } else {
        // A resposta não contém um token, pode ser um erro de autenticação
        console.log("Erro de autenticação: resposta inválida do servidor");
      }
    } catch (err) {
      this.showLoginNullPopup();
    }
  };

  onRegister = (event, login, password, role) => {
    event.preventDefault();
    request(
      "POST",
      "/auth/register",
      {
        login: login,
        password: password,
        role: role
      }).then(
      (response) => {
        setAuthHeader(response.data.token);
        this.setState({ componentToShow: "messages" });
      }).catch(
      (error) => {
        setAuthHeader(null);
        this.setState({ componentToShow: "welcome" });
      }
    );
  };

  render() {
    return (
      <div>
        <Buttons login={this.login} logout={this.logout}/>
        {this.state.componentToShow === "welcome" && <WelcomeContent />}
        {this.state.componentToShow === "messages" && <AuthContent />}
        {this.state.componentToShow === "login" && <LoginForm onLogin={this.onLogin} onRegister={this.onRegister} />}
        {this.state.isLoginNullVisible && <LoginNull onClose={this.closeLoginNullPopup} />}
      </div>
    );
  };
}
