import * as React from 'react';
import WelcomeContent from './WelcomeContent';
import AuthContent from './AuthContent';
import LoginForm from './LoginForm';
import Buttons from './Buttons';
import { request, setAuthHeader } from '../axios_helper';

export default class AppContent extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      componentToShow: "welcome"
    };
  };

  login = () => {
    this.setState({ componentToShow: "login" });
  }

  logout = () => {
    this.setState({ componentToShow: "welcome" });
  }

  onLogin = async (e, login, password) => {
    e.preventDefault();
  
    // Valide os campos de entrada (por exemplo, verifique se não estão vazios)
  
    if (!login || !password) {
      console.log("Por favor, preencha todos os campos.");
      return;
    }
  
    try {
      const response = await request("POST", "/auth/login", { login: login, password: password });
  
      // Verifique se a resposta contém um token de autenticação (ou outro sinal de sucesso)
      if (response.token) {
        // Autenticação bem-sucedida
        this.setState({ componentToShow: "messages" });
        console.log("Autenticado com sucesso");
      } else {
        // A resposta não contém um token, pode ser um erro de autenticação
        console.log("Erro de autenticação: resposta inválida do servidor");
      }
    } catch (err) {
      // Trate os erros aqui
      console.log("Erro ao fazer login:", err.message);
      this.setState({ componentToShow: "welcome" });
    }
  };

  onRegister = (event, login, password, role) => {
    event.preventDefault();
    request(
        "POST",
        "/auth/register",
        {
            login: login,
            password: password,
            role: role
        }).then(
        (response) => {
            setAuthHeader(response.data.token);
            this.setState({componentToShow: "messages"});
        }).catch(
        (error) => {
            setAuthHeader(null);
            this.setState({componentToShow: "welcome"})
        }
    );
};


  render() {
    return (
      <div>
        <Buttons login={this.login} logout={this.logout}/>
        {this.state.componentToShow === "welcome"  && <WelcomeContent />}
        {this.state.componentToShow === "messages"  && <AuthContent />}
        {this.state.componentToShow === "login"  && <LoginForm onLogin={this.login} onRegister={this.onRegister} />}
      </div>
    );

  };

}
