import React from 'react';
import './LoginNull.css';


class LoginNull extends React.Component {
  render() {
    const { onClose, messageType } = this.props;
    let message = '';

    // Determine a mensagem com base no tipo
    if (messageType === 'invalidLogin') {
      message = 'Your username or password is incorrect. Please try again.';
    } else if (messageType === 'adminError') {
      message = 'Cannot register as admin. Please contact the company.';
    } else {
      message = 'Generic message for other cases.';
    }

    return (
      <div className="overlay">
        <div className="login-popup">
          <div className="popup-content">
            <h2>{message}</h2>
          </div>
          <button className="btn btn-two" onClick={onClose}>
            Close
          </button>
        </div>
      </div>
    );
  }
}

export default LoginNull;
